import React, { useEffect, useRef, useState } from 'react';
import * as d3 from 'd3';
import { Box, IconButton, Tooltip, Paper, Typography, Snackbar, Alert } from '@mui/material';
import ZoomInIcon from '@mui/icons-material/ZoomIn';
import ZoomOutIcon from '@mui/icons-material/ZoomOut';
import RestartAltIcon from '@mui/icons-material/RestartAlt';
import FolderIcon from '@mui/icons-material/Folder';
import InsertDriveFileIcon from '@mui/icons-material/InsertDriveFile';
import CloudDownloadIcon from '@mui/icons-material/CloudDownload';
import { getFileDetails } from '../services/googleDriveService';

const MindMap = ({ data, enableDownload = false }) => {
  const svgRef = useRef(null);
  const tooltipRef = useRef(null);
  const [selectedNode, setSelectedNode] = useState(null);
  const [tooltipContent, setTooltipContent] = useState('');
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
  const [tooltip, setTooltip] = useState({ visible: false, x: 0, y: 0, content: '' });
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });
  // ÎîîÏßÄÌÑ∏ÏÑ†ÎèÑÌïôÍµê Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Í≤ΩÎ°ú
  const digitalSchoolLogoPath = '/images/digital_school_logo.png';
  // ÌòÑÏû¨ ÏãúÏ†êÍ≥º ÌôïÎåÄ Ï†ïÎèÑÎ•º Ï†ÄÏû•ÌïòÎäî ref
  const currentViewRef = useRef({ transform: null });

  useEffect(() => {
    if (!data) return;
    
    createMindMap();
    
    // Cleanup on component unmount
    return () => {
      d3.select(svgRef.current).selectAll('*').remove();
    };
  }, [data]);

  const createMindMap = () => {
    const svg = d3.select(svgRef.current);
    svg.selectAll('*').remove();
    
    const width = window.innerWidth;
    const height = window.innerHeight;
    
    const g = svg.append('g')
      .attr('transform', `translate(${width/2},${height/2})`);
    
    // Define zoom behavior
    const zoom = d3.zoom()
      .scaleExtent([0.1, 4])
      .on('zoom', (event) => {
        g.attr('transform', event.transform);
        // ÌòÑÏû¨ Î≥ÄÌôò Ï†ïÎ≥¥ Ï†ÄÏû•
        currentViewRef.current.transform = event.transform;
      });
    
    svg.call(zoom);
    
    // Ï†ÄÏû•Îêú Î≥ÄÌôò Ï†ïÎ≥¥Í∞Ä ÏûàÏúºÎ©¥ Ï†ÅÏö©, ÏóÜÏúºÎ©¥ Ï¥àÍ∏∞ ÏÉÅÌÉúÎ°ú ÏÑ§Ï†ï
    if (currentViewRef.current.transform) {
      svg.call(zoom.transform, currentViewRef.current.transform);
    } else {
      svg.call(zoom.transform, d3.zoomIdentity.translate(width/2, height/2).scale(0.8));
    }
    
    // Create radial layout
    const radius = Math.min(width, height) / 2 - 120;
    
    // Create hierarchy from data
    const root = d3.hierarchy(data);
    
    // Define custom layout (similar to radial but with modifications)
    const layout = d3.cluster()
      .size([360, radius]);
    
    // Apply layout
    layout(root);
    
    // Add links
    const linkGenerator = d3.linkRadial()
      .angle(d => d.x * Math.PI / 180)
      .radius(d => d.y);
    
    g.selectAll('.link')
      .data(root.links())
      .join('path')
      .attr('class', 'link')
      .attr('d', linkGenerator)
      .attr('fill', 'none')
      .attr('stroke', '#ccc')
      .attr('stroke-width', 1.5);
    
    // Add nodes
    const nodes = g.selectAll('.node')
      .data(root.descendants())
      .join('g')
      .attr('class', 'node')
      .attr('transform', d => `rotate(${d.x - 90}) translate(${d.y})`)
      .attr('cursor', 'pointer')
      .on('click', (event, d) => handleNodeClick(event, d))
      .on('mouseover', (event, d) => handleMouseOver(event, d))
      .on('mouseout', () => handleMouseOut());
    
    // Add node circles
    nodes.append('circle')
      .attr('r', d => d.depth === 0 ? 25 : d.data.type === 'folder' ? 15 : 8)
      .attr('class', d => {
        if (d.depth === 0) return 'central-node';
        return d.data.type === 'folder' ? 'folder-node' : 'file-node';
      })
      .attr('fill', d => {
        if (d.depth === 0) return '#8B4513';
        return d.data.type === 'folder' ? '#FFD700' : '#fff';
      })
      .attr('stroke', d => {
        if (d.depth === 0) return '#8B4513';
        return d.data.type === 'folder' ? '#DAA520' : '#2E8B57';
      })
      .attr('stroke-width', 1.5);
    
    // ÎîîÏßÄÌÑ∏ÏÑ†ÎèÑÌïôÍµê Î°úÍ≥†Î•º Ï§ëÏïô ÎÖ∏ÎìúÏôÄ Ìè¥Îçî ÎÖ∏ÎìúÏóê Ï∂îÍ∞Ä
    nodes.filter(d => d.depth === 0)
      .each(function(d) {
        const node = d3.select(this);
        
        // ÎîîÏßÄÌÑ∏ÏÑ†ÎèÑÌïôÍµê Î°úÍ≥† Ïù¥ÎØ∏ÏßÄ Ï∂îÍ∞Ä
        node.append('image')
          .attr('xlink:href', digitalSchoolLogoPath)
          .attr('width', 30)
          .attr('height', 30)
          .attr('x', -15)
          .attr('y', -15)
          .attr('transform', d => `rotate(${90 - d.x})`)
          .style('pointer-events', 'none');
      });
    
    // Ìè¥Îçî ÎÖ∏ÎìúÏóê ÏïÑÏù¥ÏΩò Ï∂îÍ∞Ä (Ï§ëÏïô ÎÖ∏Îìú Ï†úÏô∏)
    nodes.filter(d => d.data.type === 'folder' && d.depth !== 0)
      .append('text')
      .attr('dy', 5)
      .attr('text-anchor', 'middle')
      .attr('transform', d => `rotate(${90 - d.x})`)
      .html('üìÅ');
    
    // ÌååÏùº ÎÖ∏ÎìúÏóê ÏïÑÏù¥ÏΩò Ï∂îÍ∞Ä
    nodes.filter(d => d.data.type === 'file')
      .append('text')
      .attr('dy', 4)
      .attr('text-anchor', 'middle')
      .attr('transform', d => `rotate(${90 - d.x})`)
      .html('üìÑ');
    
    // Add labels
    nodes.append('text')
      .attr('dy', d => d.depth === 0 ? -35 : d.data.type === 'folder' ? -20 : -15)
      .attr('text-anchor', 'middle')
      .attr('transform', d => `rotate(${90 - d.x})`)
      .text(d => truncateText(d.data.name, 20))
      .attr('fill', '#333')
      .attr('font-size', d => d.depth === 0 ? '16px' : '12px')
      .attr('font-weight', d => d.depth === 0 ? 'bold' : 'normal');
  };

  const handleNodeClick = async (event, d) => {
    event.stopPropagation();
    
    setSelectedNode(d);
    
    // ÌååÏùº ÎÖ∏ÎìúÏù∏ Í≤ΩÏö∞
    if (d.data.type === 'file') {
      try {
        const details = await getFileDetails(d.data.id);
        
        if (enableDownload) {
          // Îã§Ïö¥Î°úÎìú Ï≤òÎ¶¨
          downloadFile(details);
        } else {
          // Í∏∞Ï°¥ Î∞©Ïãù: ÏÉÅÏÑ∏ Ï†ïÎ≥¥ ÌëúÏãú
          showFileDetails(event, details);
        }
      } catch (error) {
        console.error('ÌååÏùº Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        setNotification({
          open: true,
          message: 'ÌååÏùº Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.',
          severity: 'error'
        });
      }
    }
  };

  const downloadFile = (fileDetails) => {
    if (!fileDetails.webContentLink && !fileDetails.webViewLink) {
      // Íµ¨Í∏Ä Î¨∏ÏÑú ÌòïÏãùÏù∏ Í≤ΩÏö∞ ÎÇ¥Î≥¥ÎÇ¥Í∏∞ URL ÏÉùÏÑ±
      if (fileDetails.mimeType.includes('google-apps')) {
        let exportMimeType = '';
        let exportExt = '';
        
        // Í∞Å Íµ¨Í∏Ä Î¨∏ÏÑú ÌÉÄÏûÖÏóê ÎßûÎäî ÎÇ¥Î≥¥ÎÇ¥Í∏∞ ÌòïÏãù ÏßÄÏ†ï
        if (fileDetails.mimeType.includes('document')) {
          exportMimeType = 'application/vnd.openxmlformats-officedocument.wordprocessingml.document';
          exportExt = 'docx';
        } else if (fileDetails.mimeType.includes('spreadsheet')) {
          exportMimeType = 'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet';
          exportExt = 'xlsx';
        } else if (fileDetails.mimeType.includes('presentation')) {
          exportMimeType = 'application/vnd.openxmlformats-officedocument.presentationml.presentation';
          exportExt = 'pptx';
        } else if (fileDetails.mimeType.includes('drawing')) {
          exportMimeType = 'image/png';
          exportExt = 'png';
        } else {
          exportMimeType = 'application/pdf';
          exportExt = 'pdf';
        }
        
        // ÎÇ¥Î≥¥ÎÇ¥Í∏∞ URL
        const exportUrl = `https://www.googleapis.com/drive/v3/files/${fileDetails.id}/export?mimeType=${encodeURIComponent(exportMimeType)}`;
        
        // Ïï°ÏÑ∏Ïä§ ÌÜ†ÌÅ∞ Í∞ÄÏ†∏Ïò§Í∏∞
        const token = window.gapi.client.getToken();
        if (token) {
          // Îã§Ïö¥Î°úÎìú ÎßÅÌÅ¨ ÏÉùÏÑ± Î∞è ÌÅ¥Î¶≠
          const downloadLink = document.createElement('a');
          downloadLink.href = exportUrl + '&alt=media&access_token=' + token.access_token;
          downloadLink.download = `${fileDetails.name}.${exportExt}`;
          downloadLink.target = '_blank';
          document.body.appendChild(downloadLink);
          downloadLink.click();
          document.body.removeChild(downloadLink);
          
          setNotification({
            open: true,
            message: `"${fileDetails.name}" Îã§Ïö¥Î°úÎìú Ï§ë...`,
            severity: 'info'
          });
        } else {
          setNotification({
            open: true,
            message: 'Ïù∏Ï¶ù ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏñ¥ Îã§Ïö¥Î°úÎìúÌï† Ïàò ÏóÜÏäµÎãàÎã§.',
            severity: 'error'
          });
        }
      } else {
        setNotification({
          open: true,
          message: 'Ïù¥ ÌååÏùºÏùÄ Îã§Ïö¥Î°úÎìú ÎßÅÌÅ¨Í∞Ä Ï†úÍ≥µÎêòÏßÄ ÏïäÏäµÎãàÎã§.',
          severity: 'warning'
        });
        // ÎåÄÏã† ÏÉÅÏÑ∏ Ï†ïÎ≥¥ ÌëúÏãú
        showFileDetails(null, fileDetails);
      }
    } else {
      // ÏßÅÏ†ë Îã§Ïö¥Î°úÎìú ÎßÅÌÅ¨Í∞Ä ÏûàÎäî Í≤ΩÏö∞
      const downloadUrl = fileDetails.webContentLink || fileDetails.webViewLink;
      
      // Îã§Ïö¥Î°úÎìú ÎßÅÌÅ¨ ÌÅ¥Î¶≠
      const downloadLink = document.createElement('a');
      downloadLink.href = downloadUrl;
      downloadLink.target = '_blank';
      document.body.appendChild(downloadLink);
      downloadLink.click();
      document.body.removeChild(downloadLink);
      
      setNotification({
        open: true,
        message: `"${fileDetails.name}" Îã§Ïö¥Î°úÎìú Ï§ë...`,
        severity: 'info'
      });
    }
  };

  const showFileDetails = (event, details) => {
    // ÌååÏùº ÌÅ¨Í∏∞ Ìè¨Îß∑
    const size = details.size ? formatFileSize(details.size) : 'N/A';
    
    // ÎÇ†Ïßú Ìè¨Îß∑
    const modified = details.modifiedTime ? new Date(details.modifiedTime).toLocaleString() : 'N/A';
    const created = details.createdTime ? new Date(details.createdTime).toLocaleString() : 'N/A';
    
    // Ìà¥ÌåÅ ÏΩòÌÖêÏ∏† ÏÑ§Ï†ï
    const content = `
      <div>
        <h3>${details.name}</h3>
        <p><strong>Ïú†Ìòï:</strong> ${getMimeTypeDescription(details.mimeType)}</p>
        <p><strong>ÌÅ¨Í∏∞:</strong> ${size}</p>
        <p><strong>ÏàòÏ†ïÎê®:</strong> ${modified}</p>
        <p><strong>ÏÉùÏÑ±Îê®:</strong> ${created}</p>
        ${details.webViewLink ? `<p><a href="${details.webViewLink}" target="_blank" rel="noopener noreferrer">ÌååÏùº Ïó¥Í∏∞</a></p>` : ''}
      </div>
    `;
    
    // ÏúÑÏπò ÏÑ§Ï†ï
    const x = event ? event.pageX : window.innerWidth / 2;
    const y = event ? event.pageY : window.innerHeight / 2;
    
    // Ìà¥ÌåÅ ÌëúÏãú
    setTooltip({
      visible: true,
      x: x,
      y: y,
      content: content
    });
  };

  const handleMouseOver = (event, d) => {
    // Show a simple tooltip
    const tooltip = d3.select(tooltipRef.current);
    tooltip
      .style('display', 'block')
      .style('left', (event.pageX + 15) + 'px')
      .style('top', (event.pageY - 10) + 'px')
      .html(`<div><strong>${d.data.name}</strong><br/>${getMimeTypeDescription(d.data.mimeType)}</div>`);
  };

  const handleMouseOut = () => {
    d3.select(tooltipRef.current)
      .style('display', 'none');
  };

  const resetZoom = () => {
    const svg = d3.select(svgRef.current);
    const width = window.innerWidth;
    const height = window.innerHeight;
    
    svg.transition()
      .duration(750)
      .call(
        d3.zoom().transform,
        d3.zoomIdentity.translate(width/2, height/2).scale(0.8)
      );
  };

  const zoomIn = () => {
    const svg = d3.select(svgRef.current);
    svg.transition()
      .duration(300)
      .call(
        d3.zoom().scaleBy,
        1.3
      );
  };

  const zoomOut = () => {
    const svg = d3.select(svgRef.current);
    svg.transition()
      .duration(300)
      .call(
        d3.zoom().scaleBy,
        1 / 1.3
      );
  };

  const handleCloseTooltip = () => {
    setTooltip({ ...tooltip, visible: false });
  };

  const handleCloseNotification = (event, reason) => {
    if (reason === 'clickaway') return;
    setNotification({ ...notification, open: false });
  };

  const truncateText = (text, maxLength) => {
    if (!text) return '';
    return text.length > maxLength ? text.slice(0, maxLength) + '...' : text;
  };

  const formatFileSize = (bytes) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB', 'TB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getMimeTypeDescription = (mimeType) => {
    if (!mimeType) return 'Ïïå Ïàò ÏóÜÎäî Ïú†Ìòï';
    
    if (mimeType.includes('folder')) return 'Ìè¥Îçî';
    
    const mimeMap = {
      'application/vnd.google-apps.document': 'Íµ¨Í∏Ä Î¨∏ÏÑú',
      'application/vnd.google-apps.spreadsheet': 'Íµ¨Í∏Ä Ïä§ÌîÑÎ†àÎìúÏãúÌä∏',
      'application/vnd.google-apps.presentation': 'Íµ¨Í∏Ä ÌîÑÎ†àÏ††ÌÖåÏù¥ÏÖò',
      'application/vnd.google-apps.form': 'Íµ¨Í∏Ä ÏÑ§Î¨∏ÏßÄ',
      'application/vnd.google-apps.drawing': 'Íµ¨Í∏Ä ÎìúÎ°úÏûâ',
      'application/pdf': 'PDF Î¨∏ÏÑú',
      'image/jpeg': 'JPEG Ïù¥ÎØ∏ÏßÄ',
      'image/png': 'PNG Ïù¥ÎØ∏ÏßÄ',
      'text/plain': 'ÌÖçÏä§Ìä∏ ÌååÏùº',
      'text/html': 'HTML ÌååÏùº',
      'application/zip': 'ZIP ÏïïÏ∂ïÌååÏùº',
      'video/mp4': 'MP4 ÎπÑÎîîÏò§',
      'audio/mpeg': 'MP3 Ïò§ÎîîÏò§'
    };
    
    return mimeMap[mimeType] || mimeType;
  };

  return (
    <Box sx={{ width: '100%', height: '100%', position: 'relative' }}>
      <svg ref={svgRef} width="100%" height="100%" style={{ background: 'transparent' }}></svg>
      
      <div 
        ref={tooltipRef} 
        className="tooltip" 
        style={{ 
          display: 'none',
          position: 'absolute',
          backgroundColor: 'rgba(0, 0, 0, 0.8)',
          color: 'white',
          padding: '5px 10px',
          borderRadius: '5px',
          fontSize: '12px',
          pointerEvents: 'none',
          zIndex: 100
        }}
      ></div>
      
      {tooltip.visible && (
        <Paper
          elevation={3}
          sx={{
            position: 'absolute',
            left: tooltip.x + 15,
            top: tooltip.y - 10,
            p: 2,
            maxWidth: 300,
            zIndex: 1000,
            backgroundColor: 'rgba(255, 255, 255, 0.95)',
            borderRadius: '8px',
            boxShadow: '0 4px 20px rgba(0,0,0,0.15)'
          }}
          onClick={handleCloseTooltip}
        >
          <div dangerouslySetInnerHTML={{ __html: tooltip.content }} />
          <Typography variant="caption" sx={{ display: 'block', textAlign: 'right', mt: 1 }}>
            ÌÅ¥Î¶≠ÌïòÏó¨ Îã´Í∏∞
          </Typography>
        </Paper>
      )}
      
      <Snackbar
        open={notification.open}
        autoHideDuration={3000}
        onClose={handleCloseNotification}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleCloseNotification} severity={notification.severity} sx={{ width: '100%' }}>
          {notification.message}
        </Alert>
      </Snackbar>
      
      <Box className="zoom-controls">
        <Tooltip title="ÌôïÎåÄ">
          <IconButton onClick={zoomIn} className="zoom-button" size="large">
            <ZoomInIcon />
          </IconButton>
        </Tooltip>
        <Tooltip title="Ï∂ïÏÜå">
          <IconButton onClick={zoomOut} className="zoom-button" size="large">
            <ZoomOutIcon />
          </IconButton>
        </Tooltip>
        <Tooltip title="Í∏∞Î≥∏ Î≥¥Í∏∞">
          <IconButton onClick={resetZoom} className="zoom-button" size="large">
            <RestartAltIcon />
          </IconButton>
        </Tooltip>
      </Box>
    </Box>
  );
};

export default MindMap;
