import React, { useState, useEffect } from 'react';
import './App.css';
import MindMap from './components/MindMap';
import { Box, Button, CircularProgress, Typography, Snackbar, Alert } from '@mui/material';
import { loadGoogleDriveAPI, authenticateUser, getFolderContents } from './services/googleDriveService';

function App() {
  const [isLoading, setIsLoading] = useState(false);
  const [isSignedIn, setIsSignedIn] = useState(false);
  const [error, setError] = useState(null);
  const [data, setData] = useState(null);
  const [openSnackbar, setOpenSnackbar] = useState(false);

  const CLIENT_ID = process.env.REACT_APP_GOOGLE_CLIENT_ID || 
    '1033835493377-4gk2e80bhgsr51j6f9j9vo5mp7k7j1ad.apps.googleusercontent.com'; // 예시 ID (실제 배포 시 환경변수 사용 권장)
  const API_KEY = process.env.REACT_APP_GOOGLE_API_KEY || 
    'AIzaSyBVxLt7aRqNmDRL6NLq2CRSVKrPb3IeYIw'; // 예시 키 (실제 배포 시 환경변수 사용 권장)
  const ROOT_FOLDER_ID = '1MTFQM7oGUGDg5xYwbuuw7rwrXXfoU-a9';
  const DISCOVERY_DOCS = ['https://www.googleapis.com/discovery/v1/apis/drive/v3/rest'];
  const SCOPES = 'https://www.googleapis.com/auth/drive.metadata.readonly';

  useEffect(() => {
    loadGoogleDriveAPI(CLIENT_ID, API_KEY, DISCOVERY_DOCS, SCOPES)
      .then((gapiInitialized) => {
        if (gapiInitialized) {
          // Check if user is already authenticated
          const isAuthed = window.gapi.auth2?.getAuthInstance()?.isSignedIn.get();
          setIsSignedIn(isAuthed || false);
          
          if (isAuthed) {
            loadFolderData();
          }
        }
      })
      .catch(err => {
        setError('Google API 초기화 중 오류가 발생했습니다: ' + err.message);
        setOpenSnackbar(true);
      });
  }, []);

  const handleSignIn = async () => {
    setIsLoading(true);
    try {
      console.log('로그인 시도...');
      const success = await authenticateUser();
      console.log('로그인 결과:', success);
      setIsSignedIn(success);
      if (success) {
        loadFolderData();
      }
    } catch (err) {
      console.error('로그인 중 오류 발생:', err);
      // 오류 메시지 처리 개선
      let errorMessage = '인증 중 오류가 발생했습니다';
      if (err && err.message) {
        errorMessage += ': ' + err.message;
      } else if (err && typeof err === 'object') {
        errorMessage += ': ' + JSON.stringify(err);
      } else if (err) {
        errorMessage += ': ' + err;
      } else {
        errorMessage += ': 알 수 없는 오류';
      }
      setError(errorMessage);
      setOpenSnackbar(true);
    } finally {
      setIsLoading(false);
    }
  };

  const loadFolderData = async () => {
    setIsLoading(true);
    try {
      const rootFolderData = await fetchFolderStructure(ROOT_FOLDER_ID);
      setData(rootFolderData);
    } catch (err) {
      setError('폴더 데이터를 불러오는 중 오류가 발생했습니다: ' + err.message);
      setOpenSnackbar(true);
    } finally {
      setIsLoading(false);
    }
  };

  const fetchFolderStructure = async (folderId, depth = 0, maxDepth = 3) => {
    if (depth > maxDepth) return null;

    try {
      const folderContents = await getFolderContents(folderId);
      const folderInfo = folderContents.find(item => item.id === folderId) || {
        id: folderId,
        name: depth === 0 ? '루트 폴더' : '폴더',
        mimeType: 'application/vnd.google-apps.folder'
      };

      const children = [];
      for (const item of folderContents) {
        if (item.id === folderId) continue;

        let child = {
          id: item.id,
          name: item.name,
          type: item.mimeType.includes('folder') ? 'folder' : 'file',
          mimeType: item.mimeType
        };

        if (item.mimeType.includes('folder')) {
          const childData = await fetchFolderStructure(item.id, depth + 1, maxDepth);
          if (childData && childData.children) {
            child.children = childData.children;
          }
        }

        children.push(child);
      }

      return {
        id: folderInfo.id,
        name: folderInfo.name,
        type: 'folder',
        mimeType: folderInfo.mimeType,
        children: children
      };
    } catch (err) {
      console.error('폴더 구조를 가져오는 중 오류가 발생했습니다:', err);
      throw err;
    }
  };

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Box className="App">
      {!isSignedIn ? (
        <Box 
          sx={{ 
            display: 'flex', 
            flexDirection: 'column', 
            alignItems: 'center', 
            justifyContent: 'center', 
            height: '100vh',
            backgroundColor: '#f5f5f5' 
          }}
        >
          <Typography variant="h4" component="h1" gutterBottom>
            구글 드라이브 마인드맵
          </Typography>
          <Typography variant="body1" sx={{ mb: 4 }}>
            구글 드라이브 폴더를 시각적인 마인드맵으로 표시합니다
          </Typography>
          <Button 
            variant="contained" 
            color="primary" 
            onClick={handleSignIn}
            disabled={isLoading}
            sx={{ 
              borderRadius: '20px',
              padding: '10px 20px',
              boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
              '&:hover': {
                transform: 'translateY(-2px)',
                boxShadow: '0 6px 8px rgba(0, 0, 0, 0.15)'
              }
            }}
          >
            {isLoading ? <CircularProgress size={24} /> : '구글 계정으로 로그인'}
          </Button>
        </Box>
      ) : (
        <Box className="mindmap-wrapper" sx={{ height: '100vh', width: '100vw' }}>
          {isLoading ? (
            <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100%' }}>
              <CircularProgress />
            </Box>
          ) : data ? (
            <MindMap data={data} />
          ) : (
            <Typography variant="h6" sx={{ textAlign: 'center', mt: 4 }}>
              데이터를 불러올 수 없습니다.
            </Typography>
          )}
        </Box>
      )}
      <Snackbar open={openSnackbar} autoHideDuration={6000} onClose={handleCloseSnackbar}>
        <Alert onClose={handleCloseSnackbar} severity="error" sx={{ width: '100%' }}>
          {error}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default App;
